function advent5(input: string, size: number): number {
    for (let i = size - 1; i < input.length; i++) {
        const previous4 = input.substring(i - size, i).split('');
        const unique = [...new Set(previous4)];
        if (unique.length === size) {
            return i;
        }
    }
    return -1;
}
testInputs.forEach((testInput) => {
    const result = advent5(testInput.input, 4);
    console.log(result);
    console.assert(result === testInput.answer1, `Expected ${testInput.answer1} but got ${result}`);
});

const answer1 = advent5(inputA, 4);
console.assert(answer1 === 1235);
console.log(advent5(inputA, 4));

